input = <<EOS





EOS

deltas = {
  U: -3,
  D: 3,
  L: -1,
  R: 1,
}

illegal = {
  U: [1, 2, 3],
  D: [7, 8, 9],
  L: [1, 4, 7],
  R: [3, 6, 9],
}

code = ''
pos = 5
input.strip.split("\n").each do |line|
  line.each_char.map(&:to_sym).each do |c|
    next if illegal[c].include?(pos)
    pos += deltas[c.to_sym]
  end
  puts pos
  code += pos.to_s
end

puts code
